
# -*- coding: utf-8 -*-
# 读取ui/icon/s.png图片位base64编码
import base64

from PyQt6.QtGui import QPixmap, QImage, QIcon
from PyQt6.QtCore import QByteArray

S_PNG_BASE64 = ""

def base64_to_qpixmap(base64_str):
    # 将 Base64 字符串解码为字节数据
    byte_data = QByteArray.fromBase64(base64_str.encode())
    
    # 使用 QImage 的 fromData 静态方法来从字节数据中加载图像
    image = QImage.fromData(byte_data)
    
    # 如果需要，可以将 QImage 转换为 QPixmap
    if not image.isNull():
        pixmap = QPixmap.fromImage(image)
        return pixmap
    else:
        return None 

def base64_to_icon(base64_str):
    icon = QIcon()
    icon.addPixmap(base64_to_qpixmap(base64_str), QIcon.Mode.Normal, QIcon.State.Off)
    return icon

if __name__ == '__main__':
    
    with open('ui/icon/s.png', 'rb') as f:
        s_png = base64.b64encode(f.read()).decode()
        print(s_png)


